INSERT INTO `proposal` VALUES (38, 'Citizen Intelegent', 'produk', '<p><span style=\"color: rgba(49, 53, 59, 0.68); font-family: &quot;open sans&quot;, tahoma, sans-serif; font-size: 12px; white-space: pre-line;\">Anda dapat merasakan pekerjaan untuk membuat dan mengelola seluruh kota! Anda harus membuat kota yang menarik tempat tinggal baru, sementara pada saat yang sama menjaga peringkat persetujuan Anda dalam reputasi yang baik! Untuk melakukan ini, Anda harus berinteraksi dengan masing-masing dan setiap warga kota Anda. Tentu saja beberapa warga Anda memiliki ide lain, jadi Anda akan membuat karya Anda cocok untuk Anda membuat semua orang senang! jadilah perencana yang baik!</span><br></p>', '<p>Masyarakat</p><p>Dinas keplanologian</p><p>Gamer</p>', '<p>Mengedukasi</p><p>Memanjakan mata</p><p>Berinovasi sesuai imajinasi</p>', '<p><span style=\"color: rgb(119, 119, 119); font-family: Poppins, sans-serif;\">Promosi melalui media sosial</span></p><p><span style=\"color: rgb(119, 119, 119); font-family: Poppins, sans-serif;\">Penyuluhan ke dinas keplanologian<br></span><br></p>', '<p>Menerima kritik dan saran</p><p>Selalu menambahkan fitur menarik</p>', '<p>Mengedukasi</p><p>Memanjakan mata</p><p>Berinovasi sesuai imajinasi</p>', '<p>Mengedukasi</p><p>Memanjakan mata</p><p>Berinovasi sesuai imajinasi</p>', '<p>Menambahkan fitur</p><p>Game play menarik</p><p><br></p>', '<p>Selalu mengupdate fitur</p><p>Selalu menghadirkan gameplay terbaik</p><p><br></p>', '<p>Sumber daya manusia</p><p>Menjalin kerjasama antar stakeholder terkait</p>', 'informatika', NULL, 'upload/1594562059_POSTER.png', 'upload/1594562111_BMC.png', 24);
INSERT INTO `proposal` VALUES (39, 'Ring Light Homemade', 'produk', '<p><font face=\"Helvetica, sans-serif\">Saat ini banyak pengusaha baru atau influencer baru yang memulai bisnis kecil atau sampingan dikala sedang tidak bekerja. Tidak hanya usaha-usaha dalam skala besar dan orang-orang dewasa tetapi juga banyak usaha yang dirintis dari kalangan menegah bahkan para remaja pun ikut andil dalam berbisnis, disini UMBRAY mengambil peran sebagai usaha baru yang membuat alat pendukung pencahayaan buatan yang didirikan pada tanggal 22 Juni 2020 di Bandung.</font></p><p><font face=\"Helvetica, sans-serif\">UMBRAY memproduksi sebuah pencahayaan buatan yang disebut \"Ring Light Homemade\" bagi mereka yang ingin menampilkan produknya agar berkesan menjadi lebih baik. Selain menampilkan produk, ring light homemade UMBRAY dapat dijadikan dekorasi interior maupun eksterior. Ring light homemade ini ringan dan mudah dibawa serta memakai lampu neon flex flexibel yang elastis sehingga konsumen bebas memilih bentuk ring light yang diinginkan. Ring light homemade ini sangat cocok untuk para beauty influencer atauvlogger baru dan wirausaha. Dengan harga terjangkau dan kualitas yang bagus, ring light homemade ini siap menjadikan tampilan produk menjadi lebih baik, dan seiring berjalannya waktu semoga produk ring light homemade UMBRAY selain murah akan memiliki kualitas internasional yang masih dapat dijangkau oleh masyarakat menegah kebawah.</font></p>', '<p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Produk ini cocok untuk :</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Wanita atau pria</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Remaja / Dewasa</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Mahasiwa / i</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Influ</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">encer&nbsp;</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Wirausaha</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Semua kalangan yang gemar dengan ring light</p>', '<p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Harga produk terjangkau dan berkualitas</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Desain yang inovatif dan dapat mengikuti keinginan konsumen</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Produk bergaransi 3 bulan</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Mudah dibawa, ringan dan kuat</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Tahan air karena memakai neon flex</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Multifungsi sebagai dekorasi interior atau eksterior</p>', '<p>Media sosial : Twitter, Instagram, Facebook, Whatsup dan Line&nbsp;</p><p>E-commerce : Tokopedia &amp; Shopee</p><p>Beauty influencer</p><p><br></p>', '<p>Menjaga hubungan baik dengan customer</p><p>Promo &amp; program giveaway di media sosial</p><p>Melakukan endrosement kepada influencer</p>', '<p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Harga produk terjangkau dan berkualitas</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Desain yang inovatif dan dapat mengikuti keinginan konsumen</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Produk bergaransi 3 bulan</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Mudah dibawa, ringan dan kuat</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Tahan air karena memakai neon flex</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Multifungsi sebagai dekorasi interior atau eksterior</p>', '<p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Toko elektronik</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Toko tripod</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Toko akrilik</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Jasa kirim barang</p>', '<p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Biaya bahan lempu neon flex, akrilik, tripod dan alat&nbsp; bantu pembuatan</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Biaya pembuatan ring light</p><p style=\"margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:0cm\">Biaya kemasan &amp; packing</p>', '<p>Mencari customer</p><p>Mendesain ring light bila&nbsp; bentuk custom</p><p>Membeli bahan</p><p>Memproduksi ring light</p><p>Sortir</p><p>Packing</p>', '<p>Tempat pembuatan</p><p>Alat pembuatan</p>', 'life style', NULL, 'upload/1594531319_Poster Ring Light Homemade_Jurusan Arsitektur_Kelas D_2019-2020.jpg', 'upload/1594531686_Bisnis Canvas Ring Light Homemade_Jurusan Arsitektur_Kelas D_2019-2020.jpg', 21);
INSERT INTO `proposal` VALUES (40, 'Healthy Canteen', 'jasa', '<p>Bisnis ini berjalan dalam bidang jasa yang menyajikan masakan sehat bagi para konsumennya. Di zaman ini makanan sehat sudah jarang ditemui berbeda dengan fastfood yang sudah ada dimana-mana. Masakan yang disajikan diolah secara bersih dan higienis agar hasilnya tetap memiliki nutrisi yang dibutuhkan tubuh. Inovasi dalam bidang bisnis ini ada pada masakan yang disediakan yaitu masakan sehat yang sekarang sudah jarang ditemui karena maraknya fastfood. Oleh karena itu ide bisnis ini muncul untuk memperbaiki gizi para konsumen yang sudah terlalu lama memakan makanan fastfood.<br></p>', '<p><span lang=\"IN\" style=\"font-size:12.0pt;line-height:\n115%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:Calibri;\nmso-fareast-theme-font:minor-latin;mso-ansi-language:IN;mso-fareast-language:\nEN-US;mso-bidi-language:AR-SA\">mahasiswa dan masyarakat sekitarnya.</span><br></p>', '<p>Bergizi tinggi</p><p>Harga bersahabat dengan kantong mahasiswa</p><p>Bahan baku berkualitas dan terjamin</p>', '<p>Penjualan langsung&nbsp;</p><p>Pemesanan untuk event tertentu</p><p>Penjualan online melalui mitra gofood dan grabfood</p>', '<p>Berinteraksi langsung secara sopan dan bersahabat</p><p>Promosi di media offline maupun online</p><p>Menerima kritik dari konsumen&nbsp;&nbsp;</p>', '<p>Bergizi tinggi</p><p>Harga bersahabat dengan kantong mahasiswa</p><p>Bahan baku berkualitas dan terjamin</p>', '<p>Makanan dengan gizi yang sempurna yang berkualitas dan terjamin</p><p><br></p>', '<p>Pengeluaran untuk modal</p><p>Pengeluaran untuk promosi</p><p>Pemasukan dari pembelian makanan sehat</p>', '<p>Menjaga kualitas gizi yang seimbang dalam makanan yg disajikan</p><p>Melakukan promosi agar menarik konsumen</p><p>Mengingatkan konsumen akan hidup sehat lewat makanan sehat</p>', '<p>Bahan baku yang fresh dan berkualitas langsung dari petani</p><p>Juru masak masakan sehat</p><p>Tempat yang nyaman dan sehat</p>', 'kuliner', NULL, 'upload/1594529448_Poster Healty canteen.png', NULL, 22);
INSERT INTO `proposal` VALUES (41, 'KRERA TUMBLR CASE', 'produk', '<p>Merupakan inovasi dalam dunia desain untuk mudahkan manusia dalam beraktivitas. Sebuah tas <i>tumblr</i> yang dirancang untuk mengefektifitaskan penyimpanan botol <i>tumblr </i>dalam kegiatan sehari-hari. Kami memperkenalkan <i>KRERA tumblr case</i> dengan desain fashionable untuk mempermudah perkerjaan anda. Juga turut membantu dalam mengurangi sampah plastik untuk keberlangsungan kehidupan bumi.<br></p>', '<p>Produk ini cocok untuk:</p><p>Wanita / Pria</p><p><br></p>', '<ul><li>Menggunakan bahan waterproof</li><li>Tampilan dengan warna hitam yang simpel sehingga penggunaan KRERA tumblr case bisa dipadu padankan dengan outfit apapun atau bisa disebut juga fashionable.</li><li>Bisa mendesain strap sendiri</li><li>Terdapat beberapa kompartemen seperti Tempat menyimpan alat makan, uang, kartu atm dan handphone.</li><li>Dapat diaplikasikan pada sepeda, juga dapat digunakan dengan cara selendang pada bahu, diikat pada pinggang.</li></ul>', '<ul><li>E-commerce seperti shopee, tokopedia, lazada</li><li>Social media seperti &nbsp;instagram, twitter, facebook, line dan whatsapp</li><li>Penjualan produk secara langsung terhadap konsumen</li><li>Personal selling</li><li>Hubungan dengan pelanggan</li><li>Promo launching produk sebesar 30%</li><li>Promo buy 3 get 4</li><li>Endorsment melalui sosial media seperti instagram, twitter, dan facebook untuk menarik lebih banyak pelanggan disosial media</li></ul>', '<ul><li>Promo launching produk sebesar 30%</li><li>Promo buy 3 get 4</li><li>Endorsment melalui sosial media seperti instagram, twitter, dan facebook untuk menarik lebih banyak pelanggan disosial media</li></ul>', '<ul><li>Menggunakan bahan waterproof</li><li>Tampilan dengan warna hitam yang simpel sehingga penggunaan KRERA tumblr case bisa dipadu padankan dengan outfit apapun atau bisa disebut juga fashionable.</li><li>Bisa mendesain strap sendiri</li><li>Terdapat beberapa kompartemen seperti Tempat menyimpan alat makan, uang, kartu atm dan handphone.</li><li>Dapat diaplikasikan pada sepeda, juga dapat digunakan dengan cara selendang pada bahu, diikat pada pinggang.</li></ul>', '<ul><li>Toko kain</li><li>Penjahit</li></ul>', '<p>1. Material kain</p><p>2. Alat jahit<br></p><p>3. Upah jahit</p><p>4. Pengemasan<br></p><p>5. Pemasaran</p><p>6. Pengiriman<br></p><p>7. Gaji pegawai</p>', '<ul><li>Membeli bahan pembuatan Tumblr Case KRERA<br></li><li>Memproduksi Tumblr Case KRERA yang sangat terjamin kualitasnya</li><li>Promosi produk terhadap masyarakat secara tepat sehingga dapat membantu penjualan produk&nbsp;</li><li>Penjualan produk Tumblr Case KRERA</li></ul>', '<p>Bahan yang digunakan untuk pembuatan KRERA tumblr case yaitu kain polyester yang memiliki karakteristik tidak mudah robek, tahan lecet dan waterproof sehingga konsumen tidak perlu khawatir jika air tumpah.<br></p>', 'life style', NULL, 'upload/1594539524_KRERA TUMBLR CASE.png', 'upload/1594540202_Bisnis model canvas KRERA tumblr case.png', 21);
