INSERT INTO `proposal` VALUES (58, 'PaciL21 Gaming Buddy', 'jasa', '<p style=\"language:en-US;line-height:normal;margin-top:0pt;margin-bottom:0pt; margin-left:0in;margin-right:0in;text-indent:0in;text-align:justify;text-justify: inter-ideograph;direction:ltr;unicode-bidi:embed\"><font face=\"Source Code Pro Light\"><span style=\"font-size: 13.3333px;\">PaciL_21 Gaming Buddy adalah jasa penyedia kebutuhan game anda sehari hari, mulai dari jasa pembelian game di berbagai platform, jasa penyediaan top up in-game curency, item trade, item grinding, joki berbagai game diberbagai platform dan diberbagai genre games, yang akan dilakukan secara profesional dan dikerjakan oleh player-player berpengalaman.</span></font></p><div style=\"text-align: justify;\"><br></div>', '<p>Semua kalangan semua umur semua gender<br></p>', '<p>modal usaha yang ringan hampir tidak ada rugi nilai proposisi Baik<br></p>', '<p>Promosi di berbagai media sosisal dan platform game serta forum fanspage facebook atau reddit<br></p>', '<p>produsen dan konsumen</p>', '<p>modal usaha yang ringan hampir tidak ada rugi nilai proposisi Baik<br></p>', '<p>modal usaha yang ringan hampir tidak ada rugi nilai proposisi Baik<br></p>', '<p>UniPin, Steam, Epic Store, Sony, Indomaret, ProPlayer<br></p>', '<p>pelatihan pro player, peningkatan pelayanan, bonus pembelian<br></p>', '<p>Device berupa HP dan PC atau Laptop, serta Gaming individual Skill<br></p>', 'elektronika', NULL, 'upload/1594570504_fafa.PNG', 'upload/1594570514_fafa.PNG', 22);
INSERT INTO `proposal` VALUES (59, 'Nearby Barber', 'produk', '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-top:0cm;margin-right:0cm; margin-bottom:6.0pt;margin-left:0cm;mso-add-space:auto;line-height:150%\"><span style=\"font-size:12.0pt;line-height:150%;font-family:&quot;Times New Roman&quot;,serif\">Kita sedang berada pada zaman dimana perkembangan teknologi sangat pesat-pesatnya, hal ini dapat dilihat dari munculnya jasa secara online seperti GOJEK, GOMART, dan GOFOOD, yang bertujuan untuk memudahkan masyarakat dalam pelayanan jasa.&nbsp;</span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 12pt;\">Hal ini yang mendasari kami ingin menciptakan suatu jasa dalam bidang sistem aplikasi android yang bernama </span><i style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 12pt;\">Your Nearby Barber</i><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 12pt;\">. Aplikasi ini berfungsi sebagai sarana pencarian dan pemesanan sebuah barber shop secara online.</span></p>', '<p>Sangat Cocok di gunakan untuk para kaum muda mudi, yang terkadang mereka males untuk mengantri &amp; bahkan untuk orang\" lanjut usia&nbsp;</p>', '<p>Sangat mudah di gunakan, dan UI sangat familiar dengan GoJek yang seharusnya hampir semua orang pernah mengoperasikan Aplikasi tersebut</p>', '<p>Jalur Penjualan produk ini dari tiap transaksi yang di lakukan di dalam apps ini&nbsp;</p>', '<p>Pelanggan akan dapat mudah melakukan hubungan dengan apps ini, dengan contohnya mereka bisa berinteraksi dengan Pihak baber nya / bahkan para pemotong rambut nya&nbsp;</p>', '<p>Sangat mudah di gunakan, dan UI sangat familiar dengan GoJek yang seharusnya hampir semua orang pernah mengoperasikan Aplikasi tersebut</p>', '<p>Sangat mudah di gunakan, dan UI sangat familiar dengan GoJek yang seharusnya hampir semua orang pernah mengoperasikan Aplikasi tersebut</p>', '<p>Sangat mudah di gunakan, dan UI sangat familiar dengan GoJek yang seharusnya hampir semua orang pernah mengoperasikan Aplikasi tersebut<br></p>', '<p><br></p>', '<p>Sumber daya utama ya itu Para Pembisnis pangkas rambut yang memiliki potensi paling besar dalam sumber daya utama di apps ini, karena tanpa mereka apps ini tidak akan berjalan&nbsp;</p>', 'informatika', NULL, 'upload/1594556570_Banner_Nearby_Barber.jpg', 'upload/1594556654_Annotation 2020-07-12 192404.png', 16);
