INSERT INTO `proposal` VALUES (91, 'TAMBU', 'produk', '<p style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Tambu merupakan sebuah produk handmade hasil pengembangan dari sebuah tabung&nbsp;gambar yang sudah ada di pasaran, berfungsi sebagai wadah untuk menampung berkas berkas tugas&nbsp;yang\nakan\ndibawa agar tidak terlipat lipat. Produk ini didesain dengan menggunakan material yang&nbsp;ramah lingkungan, ukuran yang lebih bervariatif sesuai dengan ergonomis pengguna dan juga&nbsp;desain yang lebih menarik dan variatif.</p>', '<div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.31in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Pelajar&nbsp;SMK jurusan teknik gambar</div><div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.31in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Mahasiswa arsitektur dan desain<span style=\"color: rgb(0, 0, 0); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">- Mahasiswa arsitektur dan desain</span></div>', '<p style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Merupakan produk perlengkapan kuliah dengan material yang ramah\nlingkungan, dan memiliki&nbsp;kualitas produk yang tahan lama. Dengan ukuran yang bervarasi&nbsp;menyesuaikan dengan kebutuhan. Penggunannya akan sangat bersahabat dengan aktivitas mahasiswa maupun siswa&nbsp;SMK.</p>', '<div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Direct\nMarketing</div><div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\"><span style=\"text-indent: -0.19in;\">- Market\nplace</span></div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\"><div style=\"text-align: justify;\"><span style=\"text-indent: -0.19in;\">- Events Bazar / Exhibition</span></div><div style=\"text-align: justify;\"><br></div></div>', '<div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Promosi yang gencar melalui media sosial seperti instagram, twitter, facebook maupun market place</div><div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Memberikan potongan harga apabila membeli lebih dari 1pcs</div><div style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">-&nbsp;Memberikan potongan harga apabila sedang ada event tertentu</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\"><div style=\"text-align: justify;\">- Memberikan bonus berupa produk pendukung seperti&nbsp;<span style=\"text-indent: -18.24px;\">notebook</span></div><div style=\"text-align: justify;\"><br></div></div>', '<p style=\"text-align: justify; margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Merupakan produk perlengkapan kuliah dengan material yang ramah\nlingkungan, dan memiliki&nbsp;kualitas produk yang tahan lama. Dengan ukuran yang bervarasi&nbsp;menyesuaikan dengan kebutuhan. Penggunannya akan sangat bersahabat dengan aktivitas mahasiswa maupun siswa&nbsp;SMK.</p>', '<div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Produk yang tahan terhadap air</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Ringan</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Desain yang variatif</div>', '<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Biaya pembuatan tes mockup</p><p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Biaya Produksi</p><p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Biaya Promosi</p><p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Biaya Packaging</p><p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Biaya Pengiriman</p>', '<div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Pengecekan kualitas material dasar produk</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Pengecekan kualitas produk jadi</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Promosi dan pemasaran</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Customer\nservice</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Packaging\nproduk</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Pengiriman produk</div>', '<div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.31in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Produsen Bambu</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.31in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Tempat produksi</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.31in; direction: ltr; unicode-bidi: embed; word-break: normal;\">- Desainer</div><div style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.31in; direction: ltr; unicode-bidi: embed; word-break: normal;\"><span style=\"text-indent: -0.31in;\">- Kurir</span><span style=\"text-indent: -0.31in;\"> </span><span style=\"text-indent: -0.31in;\">Pengantar</span></div>', 'life style', NULL, 'upload/1594576089_POSTER TAMBU yg kecil.jpg', 'upload/1594569293_BISNIS CANVAS TAMBU.png', 21);
INSERT INTO `proposal` VALUES (94, 'Re_Chop Cycle', 'produk', '<p style=\"margin: 6pt 0cm; text-align: justify; text-indent: 35.45pt; line-height: 150%; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span lang=\"EN-ID\">Daur ulang</span></b><span lang=\"EN-ID\">&nbsp;adalah proses\nuntuk menjadikan suatu bahan bekas menjadi bahan baru dengan tujuan mencegah\nadanya&nbsp;<a href=\"https://id.wikipedia.org/wiki/Sampah\" title=\"Sampah\">sampah</a>&nbsp;yang\nsebenarnya dapat menjadi sesuatu yang berguna, mengurangi penggunaan bahan baku\nyang baru, mengurangi penggunaan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Energi\" title=\"Energi\">energi</a>,\nmengurangi&nbsp;<a href=\"https://id.wikipedia.org/wiki/Polusi\" title=\"Polusi\">polusi</a>,&nbsp;<a href=\"https://id.wikipedia.org/wiki/Kerusakan_lingkungan\" title=\"Kerusakan lingkungan\">kerusakan lahan</a>, dan\nemisi&nbsp;<a href=\"https://id.wikipedia.org/wiki/Gas_rumah_kaca\" title=\"Gas rumah kaca\">gas rumah kaca</a>&nbsp;jika\ndibandingkan dengan proses pembuatan barang baru. Daur ulang adalah salah satu\nstrategi pengelolaan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Sampah\" title=\"Sampah\">sampah</a>&nbsp;padat yang terdiri atas kegiatan\npemilahan, pengumpulan, pemprosesan, pendistribusian dan pembuatan\nproduk/material bekas pakai, dan komponen utama dalam manajemen sampah modern\ndan bagian ketiga dalam proses hierarki sampah 4<a href=\"https://id.wikipedia.org/wiki/3R\" title=\"3R\">R</a>&nbsp;(Reduce,&nbsp;<i>Reuse</i>,&nbsp;<i>Recycle,\nand Replace</i>).</span><span lang=\"EN-ID\"><o:p></o:p></span></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', 'life style', NULL, 'upload/1594570625_Poster Kewirus.JPG', NULL, 11);
INSERT INTO `proposal` VALUES (95, 'KURCACI (Kripik Udang Rebon Kacang Indonesia)', 'produk', '<p>Keripik udang rebon kacang merupakan pengembangan salah satu produk yang sudah ada.</p><p>Keripik udang rebon kacang ini merupakan makanan tradisinal yang terdiri dari udang rebon, kacang, serta rempah rempah indonesia yang membuat gurih.</p>', '<p>warung nasi, masyarakat, dan pelajar</p>', '<p>memiliki harga yang lebih murah dari pasaran dengan porsi yang cukup dan memiliki pembungkusan serta pembuatan yang higienis</p>', '<p>Melalui pihak kedua (secara online)&nbsp;</p><p>Ditawarkan secara langsung kepada pelanggan seperti warung nasi</p>', 'Memberikan pembungkusan yang baik agar kualitas makanan tetap terjaga', '<p>memiliki harga yang lebih murah dari pasaran dengan porsi yang cukup dan memiliki pembungkusan serta pembuatan yang higienis</p>', '<p>Secara pribadi, maksud pribadi disini adalah melalui modal yang di kumpulkan oleh anggota kelompok.</p>', '<p>modal 1 bungkus : Rp. 4.000,00</p><p>Modal Peralatan :&nbsp;Rp. 3.043.100</p><p>Harga Penjualan 1 bungkus : Rp. 5000,00</p><p><br></p>', '<p>Penjualan secara online (akun shopee) dan penjualan secara langsung</p>', '<p>sumber daya utama berupa bentuk fisik seperti peralatan yang dipergunakan, tempat memproduksi (rumah), serta akun penjualan online</p>', 'kuliner', NULL, 'upload/1594569719_Kurcaci.png', 'upload/1594570768_Canva proposal.png', 12);
INSERT INTO `proposal` VALUES (97, 'Atirstam', 'produk', '<p>Atirstam merupakan sebuah mobile game bertema Battle Royale yang memadukan antara LBS, Geofencing, dan Augmented Reality.<br></p>', '<p>Remaja atau lebih tua<br></p>', '<p>- Menyediakan wadah bagi pemain kasual maupun profesional</p><p>- Berpeluang menambah lapangankerja</p><p>- Berpartisipasi dalam ranah E-sport</p><p>- Mengubah persepsi warga akan game</p>', '<p>- Google</p><p>- International</p><p>- Esport Federation</p><p>- Youtube</p><p>- Perusahaan gadget (terutama smartphone)</p>', '<p>- Menyediakan wadah entertaiment</p><p>- Menjual kosmetik dalam game sebagai bentuk investasi</p><p>- Meningkatkan kinerja game&nbsp; berdasarkan&nbsp; pengalaman pemain.</p><p>- Menyediakan customer support untuk masalah game.</p>', '<p>- Menyediakan wadah bagi pemain kasual maupun profesional</p><p>- Berpeluang menambah lapangankerja</p><p>- Berpartisipasi dalam ranah E-sport</p><p>- Mengubah persepsi warga akan game</p>', '<p>- Menyediakan wadah bagi pemain kasual maupun profesional</p><p>- Berpeluang menambah lapangankerja</p><p>- Berpartisipasi dalam ranah E-sport</p><p>- Mengubah persepsi warga akan game</p>', '<p>- Pemain</p><p>- Sosial Media</p><p>- Google Play Store</p><p>- Youtube</p>', '<p>- Menyusun desain game</p><p>- Menentukan jalan cerita game</p><p>- Membuat desain kosmetik dan barang in-game lain</p><p>- Bekerja sama dengan sponsor dan pemerintah lokal</p><p>- Memperbarui game</p><div><br></div>', '<div>- Komputer dan perangkat lunak</div><div>- 2D&amp;3D Desainer</div><div>- Sound Engineer</div><div>- Voice Actor/Actress</div><div>- Programmer</div>', 'life style', NULL, 'upload/1594569439_BANNER KWU.png', 'upload/1594569515_image1712.png', 23);
INSERT INTO `proposal` VALUES (98, 'NOTRE SAVON', 'produk', '<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: 12pt; font-family: &quot;Times New Roman&quot;, serif;\"><span style=\"font-family: &quot;Calibri Light&quot;, sans-serif; color: rgb(69, 69, 69); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Notre Savon yang diambil dari Bahasa Prancis yang artinya sabun milik kita. Kenapa? Karena sabun ini bisa digunakan boleh semua kalangan. Sabun ini nyaman dan aman di semua jenis kulit. Sabun ini memiliki bentuk, aroma, dan warna yang unik dan dapat membersihkan tubuh dari bakteri dan kuman juga untuk relaksasi.</span><span style=\"font-family: &quot;Calibri Light&quot;, sans-serif;\"><o:p></o:p></span></p>', '<p style=\"margin: 0cm 0cm 7.5pt; font-family: &quot;Times New Roman&quot;, serif; text-align: justify; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 11pt; font-family: &quot;Calibri Light&quot;, sans-serif;\">Remaja (18-23 tahun) Dewasa (23-35 tahun)&nbsp;</span><span style=\"font-size: 11pt; font-family: &quot;Calibri Light&quot;, sans-serif;\">Pria dan wanita.&nbsp;</span></p><p style=\"margin: 0cm 0cm 7.5pt; font-family: &quot;Times New Roman&quot;, serif; text-align: justify; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 11pt; font-family: &quot;Calibri Light&quot;, sans-serif;\">Ekonomi</span><span style=\"font-size: 11pt; font-family: &quot;Calibri Light&quot;, sans-serif;\">: Menengah&nbsp;</span></p><p style=\"margin: 0cm 0cm 7.5pt; fon