INSERT INTO `proposal` VALUES (117, 'Sensor Sentuh', 'produk', '<p>Sensor sentuh merupakan alat yang digunakan untuk mengamankan kendaraan khusunya dalam bidang otomotif. Sensor sentuh dirangkai menggunakan beberapa komponen, seperti : transitor, optokepler, resistor, kapasitor, dioda, dan relay.<br></p>', '<p>- Pengguna kendaraan bermotor</p><p>- Semua usia</p><p>- Pengguna kendaraan bermobil<br></p>', '<p>- Modul pengaman kendaraan (kit)</p><p>- Mengamankan kendaraan (tidak bisa stater dalam keadaan kontak standby on)</p><p>- Tidak potong kabel atau tetap orisinil<br></p>', '<p>- Blog / Website</p><p>- Media sosial<br></p>', '<p>- Online</p><p>- Antar pelanggan (dengan promo)</p><p>- Mulut ke mulut<br></p>', '<p>- Modul pengaman kendaraan (kit)</p><p>- Mengamankan kendaraan (tidak bisa stater dalam keadaan kontak standby on)</p><p>- Tidak potong kabel atau tetap orisinil<br></p>', '<p>- Jasa pengiriman</p><p>- Bengkel aksesoris</p><p>- Distributor</p><p>- Seller<br></p>', '<p>- Gaji pegawai</p><p>- Biaya penitipan<br></p>', '<p>- Mempekerjakan orang</p><p>- Reseller</p><p>- Promosi<br></p>', '<p>- Berupa modul <i>plug and play</i><br></p>', 'elektronika', NULL, NULL, NULL, 12);
