INSERT INTO `proposal` VALUES (123, 'GAINTRO PROJECT', 'jasa', '<p class=\"MsoNormal\" style=\"text-align:justify;line-height:150%\"><span lang=\"EN-US\" style=\"font-size:12.0pt;line-height:150%;font-family:&quot;Times New Roman&quot;,serif\">GAINTRO\nPROJECT merupakan suatu jasa yang bergerak dalam dunia permusikan dimana kami\nmelakukan aransemen terhadap berbagai jenis lagu yang diinginkan oleh customer\ndan disesuaikan dengan keinginannya maupun disesuaikan dengan jenis musik yang\nsedang hits. Customer dapat mengupload lagu yang telah kami aransemen dan diisi\nvokal baru oleh customer itu sendiri dan jadilah suatu nuansa yang berbeda\ndengan lagu aslinya, yang diharapkan lagu tersebut akan menjadi suatu\nketertarikan tersendiri bagi para pendengarnya.<o:p></o:p></span></p><p style=\"text-align: justify; \"><span lang=\"EN-US\" style=\"font-size:12.0pt;line-height:\n107%;font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:Calibri;\nmso-fareast-theme-font:minor-latin;mso-ansi-language:EN-US;mso-fareast-language:\nEN-US;mso-bidi-language:AR-SA\"><br></span></p><p style=\"text-align: justify; \"><span lang=\"EN-US\" style=\"font-size:12.0pt;line-height:\n107%;font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:Calibri;\nmso-fareast-theme-font:minor-latin;mso-ansi-language:EN-US;mso-fareast-language:\nEN-US;mso-bidi-language:AR-SA\"><br></span><br></p>', '<p style=\"text-align: justify; \">GAINTRO Project menyasar para penyani pemula hingga penyanyi menengah yang ingin meningkatkan popularitasnya dengan meng-cover sebuah lagu yang telah kami aransemen ulang, dan disajikan dalam platform baik digital maupun konvensional.</p><div style=\"text-align: justify; \"><br></div>', '<p style=\"text-align: justify; \">GAINTRO Project menyajikan aransemen cover lagu yang lebih baru dan kekinian dengan harga yang terjangkau.<br></p>', '<p style=\"text-align: justify; \">GAINTRO Project menggunakan sosial media, serta bekerja sama dengan toko musik sebagai sarana pemasaran.</p><div><br></div>', '<p style=\"text-align: justify; \">GAINTRO Project selalu berusaha mengupdate informasi terbaru seputar aransemen lagu melalui plugin  plugin yang terdapat pada software sehingga dapat menarik peminat customer.</p><div><br></div>', '<p style=\"text-align: justify; \">GAINTRO Project menyajikan aransemen cover lagu yang lebih baru dan kekinian dengan harga yang terjangkau.<br></p>', '<p style=\"text-align: justify; \">GAINTRO Project bekerja sama dengan pemilik toko musik dan akan melebar hingga ke selebgram yang berfokus pada musik.</p><div style=\"text-align: justify;\"><br></div>', '<p style=\"text-align: justify; \">GAINTRO Project mengeluarkan cost pada berbagai macam aspek seperti Software terbaru, Brosur, Endorse, serta Peralatan Recording dan Mixing.</p><div><br></div>', '<p style=\"text-align: justify; \">GAINTRO Project memiliki aktivitas untuk memproduksi serta memasarkan karya  karya cover lagu terbaru.</p><div style=\"text-align: justify;\"><br></div>', '<p style=\"text-align: justify; \">GAINTRO Project memiliki human resources yang baik dalam bidang musik, peralatan yang memadai, serta software terbaru untuk mixing dan mastering suatu lagu.</p><div><br></div>', 'life style', NULL, 'upload/1594612318_Poster.png', 'upload/1594612426_BMC.png', 22);
INSERT INTO `proposal` VALUES (124, 'SIGHT  (ACSA WALL SHELFT)', 'produk', '<p>Sight adalah Nama Perusahaan yang didirikan 4 orang mahasiswa desain interior di ITENAS Bandung, yang mengambil mata kuliah kewirausahaan dengan memproduksi adalah barang kreatif di area ruangan, produk utama kami adalah wall SHELFT.&nbsp;</p><p>Wall SHELFT atau rak dinding kami memiliki desain yang minimalis dan fungsional. Dimana bisa dipakai dari anak-anak sampai orang dewasa. Desain kami memiliki fungsi yaitu sebagai area rak buku, gantungan kunci, baju&nbsp; dan juga untuk orang dewasa perokok kami menyediakan area asbak rokok.&nbsp;</p><p>Untuk material menggunakan kayu white kak, dengan dimensi 60x 15 cm, untuk harga berkisar 250.000-500.000 tergantung kerumitan pada desain.&nbsp;</p>', '<p>Untuk target pasar adalah dari anak- anak sampai orang dewasa, target pemasaran Jawa dan sekitarnya.&nbsp;</p><p><br></p>', '<p>Sight adlaah bisnis yang menawarkan wall SHELFT dengan desain yang menarik dan fungsional, maksudnya tidak hanya dipakai pada satu benda saja. Maka dari itu untuk menjual produk tersebut sight akan menghasilkan keuntungan bagi pemilik bisnis ini.&nbsp;</p>', '<p>Event acara</p><p>Social Media Marketing</p><p>Personal contact</p><p>Direct Marketing</p>', '<p>Promosi melalui media social : Facebook, instagram, dan Twitter</p><p>Memberikan potongan harga atau diskon pada waktu tertentu.&nbsp;</p><p>Menerima kritik dan saran konsumen dari whatsap, line dan Twitter.&nbsp;</p>', '<p>Sight adlaah bisnis yang menawarkan wall SHELFT dengan desain yang menarik dan fungsional, maksudnya tidak hanya dipakai pada satu benda saja. Maka dari itu untuk menjual produk tersebut sight akan menghasilkan keuntungan bagi pemilik bisnis ini.&nbsp;</p>', '<p>SIGHT membuka investor yang ingin menanamkan modalnya kepada kami dan juga kami membuka kerjasama dengan sponsorship terutama pada produk yang sesuai pada bidang interior.&nbsp;</p>', '<p>Penyedia bahan baku</p><p>Pemasukan kayu white oak, kayu dan lain lain</p><p>Workshop</p>', '<p>Pembelian bahan baku</p><p>Pengujian produk</p><p>Promosi pemasaran</p><p>Pengemasan produk</p><p>Pengiriman produk</p>', '<p>Digital access</p><p>Tempat produksi</p><p>Manusia&nbsp;</p><p>Designer&nbsp;</p><p>Keuangan</p><p>Pemasaran&nbsp;</p><p>Produksi&nbsp;</p><p>Alat desain&nbsp;</p><p>Laptop dan gambar kerja</p><p>Alat pengerjaan&nbsp;</p>', 'life style', NULL, NULL, NULL, 31);
