INSERT INTO `proposal` VALUES (127, 'West Java Hidden Gem Geocahing', 'jasa', '<p>Penyedia jasa wisata berbasis online, dengan menggunakan aplikasi discord. Jasa wisata ditawarkan dengan konsep geocahing, yaitu sebuah game untuk melacak suatu lokasi berdasarkan koordinat dan clue - clue. Administrator game geocahing akan menentukan titik lokasi wisata yang jarang diketahui dan memberikan koordinat, clue, dan arahan secara langsung.</p>', '<ul><li style=\"margin-top: 10pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; word-break: normal;\"><span style=\"text-indent: -0.38in;\">Wisatawan</span><span style=\"text-indent: -0.38in;\"> (</span><span style=\"text-indent: -0.38in;\">Domestik</span><span style=\"text-indent: -0.38in;\"> / </span><span style=\"text-indent: -0.38in;\">Luar</span><span style=\"text-indent: -0.38in;\"> </span><span style=\"text-indent: -0.38in;\">Negeri)</span></li><li style=\"margin-top: 10pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; word-break: normal;\"><span style=\"text-indent: -0.38in;\">Penyedia</span><span style=\"text-indent: -0.38in;\"> </span><span style=\"text-indent: -0.38in;\">Tempat</span><span style=\"text-indent: -0.38in;\"> </span><span style=\"text-indent: -0.38in;\">Wisata</span><span style=\"text-indent: -0.38in;\"> (</span><span style=\"text-indent: -0.38in;\">jika</span><span style=\"text-indent: -0.38in;\"> </span><span style=\"text-indent: -0.38in;\">ingin</span><span style=\"text-indent: -0.38in;\"> </span><span style=\"text-indent: -0.38in;\">mengiklankan</span><span style=\"text-indent: -0.38in;\">)</span><span style=\"text-indent: -0.38in; font-size: 20pt; font-family: &quot;Century Gothic&quot;; color: white;\">)</span></li></ul>', '<ul><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Lokasi wisata yang jarang diketahui</li><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Layanan yang\ninteraktif</li><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Adanya Community sharing</li></ul>', '<ul><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Pemasaran melaui\naplikasi\npemasaran\npenyedia\njasa\nseperti\nOLX.</li><li>\n\nSocial\nmedia marketing<br></li></ul>', '<p style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Dengan adanya discord group dan\ngame master, setiap peserta akan diarahkan dan dibimbing selama permainan geocaching berlangsung, dan setiap peserta akan dimintan feed back setelah game selesai.</p>', '<ul><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Lokasi wisata yang jarang diketahui</li><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Layanan yang\ninteraktif</li><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Adanya Community sharing</li></ul>', '<ul><li style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0.38in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Pengelola Tempat Wisata</li><li style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0.38in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Dinas\nPariwisata</li><li style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0.38in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Warga\nSetempat yang dekat\ndengan lokasi wisata dan perangkat keamanan daerahnya</li></ul>', '<ul><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Admin (game master)</li><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Tim Riset / Survei</li></ul>', '<ul><li style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Update lokasi\nwisata</li><li>Geocahing\nadmin (game master)<br></li></ul>', '<ul><li style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0.38in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Social media HUB (Discord : gaming social media, Intagram, dll)</li><li style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0.38in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Tim Riset / Survei</li><li style=\"margin-top: 10pt; margin-bottom: 0pt; margin-left: 0.38in; direction: ltr; unicode-bidi: embed; word-break: normal;\">Google Map</li></ul>', 'life style', NULL, 'upload/1594625868_04_Poster_Kewirausahaan_Kelompok4.png', NULL, 23);
