INSERT INTO `proposal` VALUES (134, 'Shoes Cleaner (sabun cuci sepatu)', 'produk', '<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Perkembangan bisnis laundry di indonesia semakin berkembang pesat seiring dengan banyaknya&nbsp;&nbsp;inovasi baru mengenai apa yang menjadi kebutuhan masyarakat saat ini, Akhir-akhir ini kita banyak menemukan inovasi yaitu salah satu nya laundry sepatu yang saat ini mulai menjamur di indonesia, Lalu dengan merambahnya bisnis fashion produk lokal terutama di segmen alas kaki banyak sekarang kolektor sepatu yang mulai resah akan perawatan mengenai sepatu nya .</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">&nbsp; &nbsp; &nbsp; Shoes Cleaner by Different Looks merupakan usaha yang bertujuan untuk memenuhi kebutuhan bisnis laundry dan para sneakerhead dalam membersihkan dan merawat sepatu.&nbsp;</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">&nbsp; &nbsp; &nbsp;&nbsp;<b>Sabun yang kita jual tersebut merupakan berjenis cair yang penggunaan nya aman dan tidak merusak sepatu.</b></span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><o:p>&nbsp;</o:p></p>', '<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Segmentasi Pasar Produk</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Dalam segmentasi produk kami adalah untuk semua kalangan bawah, menengah, dan atas .</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Target Pemasaran&nbsp;</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Produk ini akan kami targetkan untuk umum terutama pebisnis laundry , penjual sepatu bekas , dan kolektor sepatu (sneakerhead)<o:p></o:p></span></p>', '<p>- Harga terjangkau</p><p>- Kemasan aman ditempatkan dimana saja</p><p>- Sudah teruji dengan berbagai percobaan yang kami lakukan</p>', '<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Strategi Promosi dan Pemasaran</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\">Awalnya produk kami akan ditawarkan ke beberapa laundry, lalu kami juga menggunakan media sosial seperti WA, Instagram , dan lainnya .</span><o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt; font-size: medium; font-family: Calibri, sans-serif;\"><o:p>&nbsp;</o:p></p>', '<p>Via social network dan Tatap muka langsung</p>', '<p>- Harga terjangkau</p><p>- Kemasan aman ditempatkan dimana saja</p><p>- Sudah teruji dengan berbagai percobaan yang kami lakukan</p>', '<p>Different Looks Shoes Clean and Care</p>', '<p>Melalui dana patungan setiap anggota</p>', '<p>- Tanggung jawab terhadap produk yang di jual</p><p>- Tidak putus asa saat produk di pasaran tidak laku</p><p>- Mencari solusi saat ada ancaman yang datang</p>', '<p>- Manusia</p><p>- Material</p>', 'life style', NULL, 'upload/1594638982_WhatsApp Image 2020-07-13 at 5.53.31 PM.png', NULL, 22);
INSERT INTO `proposal` VALUES (135, 'NAINI KOPI', 'produk', '<p>Sebuah inovasi kopi susu kekinian dengan menggunakan susu lowfat dan varian rasa kelapa dengan topping nata de coco. selain itu ada varian rasa lain dengan topping yang berbeda.</p>', '<p>Mahasiswa/kalangan muda pecinta kopi</p>', '<p>MENCIPTAKAN PENGALAMAN\nKONSUMEN MENIKMATI KOPI\nMELALUI KEMUDAHAN PESAN\nONLINE,&nbsp;MANGANTAR PESANAN\nPADA PELANGGAN\nMAMPU BERSAING\nDENGAN BISNIS KOPI\nYANG LAIN<br></p>', '<p><br></p>', '<p><br></p>', '<p>MENCIPTAKAN PENGALAMAN\nKONSUMEN MENIKMATI KOPI\nMELALUI KEMUDAHAN PESAN\nONLINE,&nbsp;MANGANTAR PESANAN\nPADA PELANGGAN\nMAMPU BERSAING\nDENGAN BISNIS KOPI\nYANG LAIN<br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', '<p><br></p>', 'kuliner', NULL, 'upload/1594640657_WhatsApp Image 2020-07-13 at 18.24.52.jpeg', 'upload/1594640698_0001.jpg', 21);
INSERT INTO `proposal` VALUES (136, 'Croponics : Toko Hidroponik Berbasis Lokasi', 'jasa', '<p>CROPONICS merupakan aplikasi yang dibuat untuk perangkat bergerak seperti : <i>smartphone, tablet, </i>dan lainnya. Aplikasi ini memberikan layanan pembelian sayuran hidoponik langsung dari petani hidroponik secara <i>online</i>. Selain memberikan layanan jual-beli, aplikasi ini juga dibuat dengan beberapa fitru informasi diantaranya menyertakan kandungan dan informasi gizi dari produk yang dipasarkan, pemesanan yang ditawarkan bersifat <i>pre-order</i> agar sayur yang dijual dapat diperoleh dalam bentuk <i>fresh</i>.<br></p>', '<ul><li>Masyarakat</li><li>Ibu Rumah Tangga/ Asisten Rumah Tangga</li><li>Restoran dan Rumah Makan</li><li>Hotel</li><li>Supermarket</li><li>dan Rumah Sakit<br></li></ul>', '<ul><li>Memberikan layanan pembelian sayuran hidroponik langsung dari petani secara <i>online</i>.</li><li>Sistem pemesanan yang ditawarkan bersifat <i>pre-order</i> sehinggga meminimalisir bahan yang terbuang dan produk yang dipasarkan dalam bentuk <i>fresh.</i></li><li>Menyertakan kandungan dan informasi gizi dari produk yang dipasarkan.</li><li>Untuk keperluan bisnis seperti rumah makan, hotel, dsb akan diberikan potongan harga yang sesuai dengan kuantitas pemesanan<br><i></i><br></li></ul>', '<ul><li>Online: media sosial seperti Line, Instagram, WhatsApp, Facebook, Twitter, dan Youtube.</li><li>Offline: Pameran atau <i>event</i>, dan testimoni dari konsumen (<i>word of mouth or costumer to costumer</i><br></li></ul>', '<ul><li><p>\n\n</p>\n\n\n\n\n\nBantuan personal: komunikasi secara langsung melalui media <i>chatting </i>di aplikasi.</li><li>Layanan <i>delivery order.</i></li><li>Penambahan bonus atau diskon melaui notifikasi pesan di akun pengguna dan <i>email</i>.</li><li>Penambahan kolom <i>feedback</i> pada aplikasi.<br><i></i><br>\n\n</li></ul>', '<ul><li>Memberikan layanan pembelian sayuran hidroponik langsung dari petani secara <i>online</i>.</li><li>Sistem pemesanan yang ditawarkan bersifat <i>pre-order</i> sehinggga meminimalisir bahan yang terbuang dan produk yang dipasarkan dalam bentuk <i>fresh.</i></li><li>Menyertakan kandungan dan informasi gizi dari produk yang dipasarkan.</li><li>Untuk keperluan bisnis seperti rumah makan, hotel, dsb akan diberikan potongan harga yang sesuai dengan kuantitas pemesanan<br><i></i><br></li></ul>', '<br>', '<p>\n\n</p>\n\n\n\n\n\n<br>', '<p>\n\n</p>\n\n\n\n\n\n<br>', '<p>\n\n</p>\n\n<br>', 'agrobisnis', NULL, 'upload/1594641959_WhatsApp Image 2020-07-12 at 20.17.20.png', 'upload/1594641983_Model Canvas.png', 23);
INSERT INTO `proposal` VALUES (137, 'Alat Relaksasi Portable', 'produk', '<p style=\"text-align: justify; \">&nbsp; &nbsp; &nbsp; &nbsp; Alat relaksasi <i>portable </i>adalah suatu alat dengan desain dari kayu dan resin yang berfungsi sebagai alat relaksasi dengan kemampuan dapat mengeluarkan suara, aroma terapi, dan cahaya yang dapat membuat nyaman. Kayu yang akan digunakan merupakan limbah kayu sehingga ramah lingkungan. Resin digunakan sebagai efek untuk diorama yang dihasilkan dengan kayu agar terlihat indah dan menarik.</p><p style=\"text-align: justify; \">&nbsp; &nbsp; &nbsp; &nbsp; Alat ini dilengkapi dengan <i>aromatherapy humidifier </i>sebagai pengharum ruangan dan <i>speaker </i>untuk menambah suasan tenang bagi pengguna. Kedua komponen ini terdapat di dalam kotak kayu di bawah lampu.</p><p style=\"text-align: justify; \">&nbsp; &nbsp; &nbsp; &nbsp; Target pasar dari alat ini adalah generasi muda yang memiliki aktivitas padat dan tidak memiliki cukup waktu atau biaya untuk melakukan <i>refreshing</i>, serta penikmat seni yang menyukai seni visual. Alat ini akan dipasaran melalui <i>online marketplace</i> dengan sistem pre-order.</p><p style=\"text-align: justify; \">&nbsp; &nbsp; &nbsp; &nbsp; Kualitas alat relaksasi ini dapat diuji dengan menekan tombol <i>switch</i> masing-masing komponen; lampu hias, <i>aromatherapy humidifier</i>, dan <i>speaker</i>.<br></p>', '<p>1. Laki-laki dan perempuan berusia 20 - 40 tahun</p><p>2. Berpenghasilan lebih dari Rp 2.000.000/bulan</p><p>3. Tinggal di daerah perkotaan atau daerah lainnya yang cenderung panas</p><p>4. Penikmat seni, cenderung penyendiri, memiliki rutinitas padat dan berat&nbsp;</p>', '<ul><li>Lampu hias dilengkapi <i>aromatherapy humidifier</i> dan <i>speaker</i></li><li>Desain dengan diorama dari kayu dan resin&nbsp;</li><li>Dapat menstimulasi mata, hidung, dan telinga</li><li>Bahan dari limbah kayu ramah lingkungan</li><li>Pelayanan cepat dan responsif</li></ul>', '<ul><li>Media sosial</li><li><i>Website</i></li><li><i>Online marketplace</i></li></ul>', '<ul><li>Facebook</li><li>Twitter</li><li>Instagram</li><li>Line@</li></ul>', '<ul><li>Lampu hias dilengkapi <i>aromatherapy humidifier</i> dan <i>speaker</i></li><li>Desain dengan diorama dari kayu dan resin&nbsp;</li><li>Dapat menstimulasi mata, hidung, dan telinga</li><li>Bahan dari limbah kayu ramah lingkungan</li><li>Pelayanan cepat dan responsif</li></ul>', '<ol><li><i>Supplier </i>limbah kayu dan resin</li><li><i>Supplier </i>komponen elektronika</li><li>Kontraktor</li><li><i>Buzzer</i> media sosial</li><li>Penyedia jasa ekspedisi</li></ol>', '<ul><li>HPP (bahan baku, operasional, investasi alat, dll)</li><li>Sewa tempat produksi dan kantor</li><li>Royalti</li><li>Sedekah</li></ul>', '<ol><li>Mendesain dan membuat alat relaksasi <i>portable</i></li><li>Menjaga kualitas produk</li><li>Melakukan <i>branding </i>dan <i>selling</i></li></ol>', '<ol><li>Logistik</li><li>Tempat produksi</li><li>Kantor</li><li>SDM (<i>manager</i>, <i>marketing</i>, desainer produk, <i>programmer</i>)</li></ol>', 'elektronika', NULL, 'upload/1594642509_Banner - Alat Relaksasi Portable.jpeg', 'upload/1594642531_BMC - Alat Relaksasi Portable.jpeg', 11);
